###############################################################################TTGO T-Display采用的是240x135的分辨率1.14英寸的ST7789V IPS显示屏#而驱动芯片总是以320x240为基准读取内存并显示的#所以显示屏只是显示了(52，40)-(186，279)这个区域内的内容##############################################################################import machine, display, utime, math#取补色函数，默认24bit，按位取反，不考虑正负def turn(h,max=0xFFFFFF):    return max-htft = display.TFT()#显示屏初始化tft.init(tft.ST7789, width=320, height=240, rot=tft.LANDSCAPE, miso=17, mosi=19, clk=18, cs=5, dc=16, rst_pin=23, backl_pin=4, backl_on=1)tft.setwin(40,52,279,186)#设置前景和背景色tft.set_bg(turn(tft.BLUE))tft.set_fg(turn(tft.GREEN))#清除屏幕tft.clear()#画矩形定位屏幕tft.rect(0,0,20,20)tft.rect(221,116,20,20,turn(tft.YELLOW),turn(tft.PINK))#绘制图形tft.ellipse(120,67,120,67)tft.line(0,0,240,135)#显示文字tft.font(tft.FONT_Ubuntu)tx = "hello"tft.text(30, 0, tx)#显示数码管数字tft.font(tft.FONT_7seg,outline=0xFFFFFF)tx= "1234567890"tft.text(tft.CENTER,60,tx)